package ncanode_test

import (
	"testing"

	"github.com/danikarik/ncanode-go"
	"github.com/stretchr/testify/require"
)

func TestPKCS12Info(t *testing.T) {
	client, err := ncanode.NewClient("http://127.0.0.1:14579")
	require.NoError(t, err)

	testCases := []struct {
		Name       string
		P12        string
		Password   string
		VerifyOCSP bool
		VerifyCRL  bool
	}{
		{
			Name:       "Default",
			P12:        "",
			Password:   "Qwerty12",
			VerifyOCSP: false,
			VerifyCRL:  false,
		},
	}

	for _, tc := range testCases {
		t.Run(tc.Name, func(t *testing.T) {
			resp, err := client.PKCS12Info(tc.P12, tc.Password, tc.VerifyOCSP, tc.VerifyCRL)
			require.NoError(t, err)
			require.False(t, resp.Result.Valid)
			require.Equal(t, ncanode.AUTH, resp.Result.KeyUsage)
			require.Equal(t, "122684438670642568061334282296011886211357830154", resp.Result.SerialNumber)
		})
	}
}
